import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useForm } from "react-hook-form";
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from "yup";
import { ToastContainer, toast } from 'react-toastify';

const schema = yup.object({
    first_name: yup.string().min(4, "Names be atleast 4 characters long").required(),
    last_name: yup.string().min(4, "Names be atleast 4 characters long").required(),
    username: yup.string().min(4, "Names be atleast 4 characters long").required(),
    email: yup.string().email().required(),
    number: yup.string().required(),
    password: yup.string().min(6, 'Password must contain 8 or more characters').required(),
    password_confirmation: yup.string().oneOf([yup.ref('password'), null], 'Passwords must match').required()
  }).required();

export default function Register({getUserData, isLoggedIn}){
    const [serverErrors, setserverErrors] = useState({})
    const { register, handleSubmit, formState:{ errors } } = useForm({
        resolver: yupResolver(schema)
      });
    const onSubmit = (data) => handleUserRegistration(data);

    const navigate = useNavigate()

    function handleUserRegistration(userData){      
        fetch("http://localhost:9292/login", {
        method: "POST",
        headers: {
            'content-type': 'application/json',
        },
        body: JSON.stringify(userData)
        })
        .then(res => res.json())
        .then(data => {
            if (data.error) {
                console.log(data);
            setserverErrors(true)
            toast.error(`${data.error}`, {
                position: "top-center",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: false,
                progress: undefined,
                theme: "colored",
            });
        }
        else{
            console.log(data);
            setserverErrors(false)
            toast.success(`Welcome back ${data.username}`, {
                position: "top-center",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: false,
                progress: undefined,
                theme: "colored",
            });
            sessionStorage.setItem("user_id", JSON.stringify(data.id))
            getUserData(data)
        }
      })         
    }

    return(
        <>
        <div className="container m-5">
            <div className="register-container">
                <ToastContainer />
                <div className="main-register">
                    <form onSubmit={handleSubmit(onSubmit)}>
                        <div className="">
                            {serverErrors ? <p className='error-message'>Invalid username or password</p> : false}
                            <div className="input-group">
                                <div className="mb-4">
                                    <label htmlFor="first_name">First Name</label>
                                    <input id='first_name' {...register("first_name")} className="form-control my-2" 
                                    type="text" placeholder="eg. john" />
                                    <p className='error-message'>{errors.first_name?.message}</p>
                                </div>

                                <div className="mb-4">
                                    <label htmlFor="last_name" className='input-left'>Last Name</label>
                                    <input id='last_name' {...register("last_name")} className="form-control my-2 input-left" 
                                    type="text" placeholder="eg. Doe" />
                                    <p className='error-message input-left'>{errors.last_name?.message}</p>
                                </div>
                            </div>

                            <div className="input-group">
                                <div className="mb-4">
                                    <label htmlFor="username">Username</label>
                                    <input id='username' {...register("username")} className="form-control my-2" 
                                    type="text" placeholder="eg. john_doe" />
                                    <p className='error-message'>{errors.username?.message}</p>
                                </div>
                                
                                <div className="mb-4">
                                    <label htmlFor="email" className='input-left'>Email</label>
                                    <input id='email' {...register("email")} className="form-control my-2 input-left" 
                                    type="email" placeholder="eg. john.doe@mail.com" />
                                    <p className='error-message input-left'>{errors.email?.message}</p>
                                </div>
                            </div>

                            <div className="input-group">
                                <div className='h-100 mb-3'>
                                    <label htmlFor="password">Password</label>
                                    <input id='password' {...register("password")} className="form-control my-2" 
                                    type="password" placeholder="Password" />
                                    <p className='error-message'>{errors.password?.message}</p>
                                </div>

                                <div className='h-100 mb-3'>
                                    <label htmlFor="password_confirmation" className='input-left'>Confirm Password</label>
                                    <input id='password_confirmation' {...register("password_confirmation")} className="form-control my-2 input-left" 
                                    type="password" placeholder="Confirm your password" />
                                    <p className='error-message input-left'>{errors.password_confirmation?.message}</p>
                                </div>
                            </div>

                            <div className="description justify-content-end gap-2 my-2">
                                <button className="register-button" type="submit">Register</button>
                            </div>

                            <p className="mt-4">Already have an account? <Link className='login-here' to="/login">Login here</Link></p>
                        </div>
                    </form>
                </div>

                <div className="register-info">
                    <h3 className="register-heading text-left mt-3">Create Account</h3>
                    <p className='text-muted'>Welcome to your health dashboard. Get started
                    today by providing your details.Please ensure that your phone number
                    is valid as we will use it for account verification.</p>
                </div>
            </div>
        </div>
    </>
)
}
