import { useState } from "react";
import { Link } from "react-router-dom";
import '../../assets/css/login.css';
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { useForm } from "react-hook-form";

const schema = yup
  .object({
    username: yup.string().required(),
    password: yup.string().required(),
  })
  .required();

export default function Login({ handleLogIn }) {
  const [serverErrors, setserverErrors] = useState({});
  
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });
  const onSubmit = (data) => handleUserLogin(data);

  function handleUserLogin(userData){       
    fetch("https://auth.larntechke.com/auth/login", {
        method: "POST",
        headers: {
            'content-type': 'application/json',
        },
        body: JSON.stringify(userData)
    })
    .then(res => {
        if (res.status !== 200) {
            res.json().then(data => {
                setserverErrors(data)
            })
        }
        else{
            res.json().then(data => {
              console.log(res.headers.get("x-auth-token"));
              sessionStorage.setItem("token", JSON.stringify(res.headers.get("x-auth-token")));
              setserverErrors({})
              handleLogIn();
            })
        }
    })
    
}
  return (
    <div className="log container-full">
      <div className="image">
       
      </div>
      <div className="password">
        <div className="logo">
          
        
        </div>
        <div className="assign">
          <form className="user" onSubmit={handleSubmit(onSubmit)}>
            <h1 className="signIn">Sign In</h1>
            {serverErrors? <p className='text-danger'>{serverErrors.error_message}</p> : false}
            <div className="userInput">
              <label className="Label">Username</label>
              <input
                id="username"
                {...register("username")}
                className="form-control my-2"
                type="text"
                placeholder="eg. john_doe"
              />
              <p className="register-error-message">
                {errors.username?.message}
              </p>
            </div>
            <div className="passwordInput">
              <div className="Input">
                <label className="Label">Password</label>
                <input
                  id="password"
                  {...register("password")}
                  className="form-control my-2"
                  type="password"
                  placeholder="Password"
                />
                <p className="register-error-message">
                  {errors.password?.message}
                </p>
              </div>
            </div>
            <button className="btn" type="submit">
              Sign In{" "}
              <span className="svg">
                <svg
                  width="16"
                  height="16"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke-width="1.5"
                  stroke="currentColor"
                >
                  <path
                    stroke-linecap="round"
                    stroke-linejoin="round"
                    d="M4.5 12h15m0 0l-6.75-6.75M19.5 12l-6.75 6.75"
                  />
                </svg>
              </span>
            </button>
            <div className="linkDetails">
              <div className="loginContainer">
                <p>
                  or{" "}
                  <Link className="create" to="/register">
                    Create an Account
                  </Link>
                </p>
              </div>
              <div className="recover">
                <p>
                  Forgot your Password?{" "}
                  <a className="create" href="#create">
                    Recover it
                  </a>
                </p>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>

 
  );
}
