import { Link, useParams } from "react-router-dom";

export default function Single({price, address, image, name, posted, user, features, bed, wifi, user_id, size, booked, electronics, type}){
    const {id} = useParams()

    const days = Math.ceil((new Date().getTime() - new Date(posted).getTime()) / 86400000)

    return(
        <>
        <div className="itemDisplayWrapper">
            <div style={{
                backgroundImage: "url(" + image + ")",
                backgroundPosition: "center",
                backgroundRepeat: "no-repeat",
                backgroundSize: "100% 100%"
            }} className="itemDisplayImg">     
            <div className="itemDisplayHeart">
            </div>
        </div>
    
        <div className="itemDisplayDetailLines">
            <div className="itemDisplayPropName">
                <center>{name}</center>
            </div>
            
            <span className="description"><small>{features}</small></span>
            <div className="">
            <i className="fa fa-object-ungroup" aria-hidden="true"></i> {size} Sq.Ft.
            </div>
            <div className="">
            <i class="fas fa-calendar-day"></i> Posted {days >= 2 ? days + " days" : " day"} ago
            </div>
            <div className="">
            <i className="fa fa-bed" aria-hidden="true"></i> x{bed}  &nbsp; &nbsp; &nbsp;
            <i className="fa fa-bath" aria-hidden="true"></i> x{bed}   &nbsp; &nbsp; &nbsp;
            <i class="fas fa-tv"></i> x4   &nbsp; &nbsp; &nbsp;
            <i class="fas fa-wifi"></i> x4   &nbsp; &nbsp; &nbsp;
            <i class="fas fa-microchip"></i> x4   &nbsp; &nbsp; &nbsp;
            </div>

            <div className=""><i className="fa fa-map-marker" aria-hidden="true"></i>  {address}</div>
            <div className=""><i className="fa fa-money" aria-hidden="true"></i> KES. {price}</div>
            <div className=""><i className="fa fa-building-o" aria-hidden="true"></i> by {user_id}</div>
            <div className="my-3">
            <Link
             type="button" 
             to={booked? false: `/rooms/${id}/order`} 
             className="exploreBtn text-center">{booked? "Already Booked" : "Order"}</Link> 
            </div>
        </div>
        </div>
 
        </>
    )
}
