import { useState } from "react";
import { Link } from "react-router-dom";
// import signupLogo from "../../assets/auth.jpg"
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { useForm } from "react-hook-form";
import { ToastContainer, toast } from 'react-toastify';


const schema = yup
  .object({
    username: yup.string().required(),
    password: yup.string().required(),
  })
  .required();

export default function Login() {
  const [serverErrors, setserverErrors] = useState(false);
  
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });
  const onSubmit = (data) => handleUserLogin(data);

  function handleUserLogin(userData){     
    fetch("http://localhost:9292/login", {
        method: "POST",
        headers: {
            'content-type': 'application/json',
        },
        body: JSON.stringify(userData)
    })
    .then(res => res.json())
    .then(data => {
        if (data.error) {
            console.log(data);
            setserverErrors(true)
            toast.error('ðŸ¦„ Wow so easy!', {
                position: "top-center",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: false,
                progress: undefined,
                theme: "colored",
            });
        }
        else{
            console.log(res);
            res.json().then(data => {
                console.log(data);
                setserverErrors(false)
            })
        }
    })    
}
  return (
    <div className="login-container">
        <ToastContainer />
        <div className="main-login">
          <form className="user" onSubmit={handleSubmit(onSubmit)}>
            <h1 className="signIn">Sign In</h1>
            {serverErrors ? <p className='error-message'>Invalid username or password</p> : false}
            <div className="userInput">
              <label className="Label">Username</label>
              <input
                id="username"
                {...register("username")}
                className="form-control my-2"
                type="text"
                placeholder="eg. john_doe"
              />
              <p className="error-message">
                {errors.username?.message}
              </p>
            </div>
            <div className="passwordInput">
              <div className="Input">
                <label className="Label">Password</label>
                <input
                  id="password"
                  {...register("password")}
                  className="form-control my-2"
                  type="password"
                  placeholder="Password"
                />
                <p className="error-message">
                  {errors.password?.message}
                </p>
              </div>
            </div>
            <button className="btn" type="submit">
              Sign In{" "}
              <span className="svg">
                <svg
                  width="16"
                  height="16"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke-width="1.5"
                  stroke="currentColor"
                >
                  <path
                    stroke-linecap="round"
                    stroke-linejoin="round"
                    d="M4.5 12h15m0 0l-6.75-6.75M19.5 12l-6.75 6.75"
                  />
                </svg>
              </span>
            </button>
            <div className="linkDetails">
              <div className="loginContainer">
                <p>
                  or{" "}
                  <Link className="create" to="/register">
                    Create an Account
                  </Link>
                </p>
              </div>
              <div className="recover">
                <p>
                  Forgot your Password?{" "}
                  <a className="create" href="#create">
                    Recover it
                  </a>
                </p>
              </div>
            </div>
          </form>
        </div>
    </div>

 
  );
}
